import React, { useCallback, useEffect, useState, Fragment } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import Sitebar from './home/Navbar';
import Auth from './Auth/Auth';
import BeerIndex from './Beers/BeerIndex'
import Navbar from './home/Navbar'
import { BrowserRouter as Router } from 'react-router-dom';
import AllPosts from './Beers/AllPosts';
import { Route, Link, Switch } from 'react-router-dom'
import MyPosts from './Beers/MyPosts'







function App() {
const [sessionToken, setSessionToken] = useState('');

useEffect(() => {
  if (localStorage.getItem('token')){
    setSessionToken(localStorage.getItem('token'));
  }
}, [])

const updateToken = (newToken) => {
  localStorage.setItem('token', newToken);
  setSessionToken(newToken);
  console.log(sessionToken)
}

const clearToken = () => {
  localStorage.clear();
  setSessionToken('');
}

const protectedViews = () => {
  return (sessionToken === localStorage.getItem('token') ? <BeerIndex token={sessionToken}/>
  : <Auth updateToken={updateToken}/>)
}

  return (
    <>

    <div className="App" style={{backgroundColor: '#3D7068' }}>
      <Sitebar clickLogout={clearToken}/>
      {protectedViews()}
    </div>

  <Router>
    <Sitebar />
  </Router>


  <div className="router">
<ul className="sidebar-list list-unstyled">
  <li><Link to="/">All Posts</Link></li>
  <li><Link to="/myposts">My Posts</Link></li>

</ul>


<Switch>
  <Route exact path="/allposts"><AllPosts/></Route>
  <Route exact path="/myposts"><MyPosts /></Route>


</Switch>
</div>






    </>
  );
}

export default App;
