import React, { useState, useEffect } from "react";
import { Table, CardColumns } from "reactstrap";
import APIURL from "../helpers/enviornments";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";
import BeerEdit from "./BeerEdit";

const MyPosts = (props) => {
  const [beer, setBeer] = useState([]);
  useEffect(() => {
  fetch(`http://localhost:3001/beer/mine`, {
    method: "GET",
    headers: new Headers({
      "Content-Type": "application/json",
      Authorization: props.token,
    }),
  })
    .then((res) => res.json())
    .then((logData) => {
      setBeer(logData);
      console.log(logData);

    });



    }, [])

  const myMapper = () => {
      if (beer)
    return  beer.map((beer, index) => { 
      return (
        <CardColumns>
          <Card>
            <CardImg top width="100%" alt="Card image cap" />
            <CardBody>
              <CardTitle>{beer.name}</CardTitle>
              <CardSubtitle>{beer.brewery}</CardSubtitle>
              <CardSubtitle>{beer.abvibu}</CardSubtitle>
              <CardSubtitle>{beer.location}</CardSubtitle>
              <CardText>{beer.comments}</CardText>
              <BeerEdit />
              <Button color="danger">Delete</Button>
            </CardBody>
          </Card>
        </CardColumns>
      );
    });
  };
  return (
    <>
      {myMapper()}
    </>
  );
};

export default MyPosts;
