import React, {useState} from 'react';
import { Navbar, NavbarBrand, NavbarToggler, NavItem, Button, Nav, Collapse } from "reactstrap";
import BeerCreate from '../Beers/BeerCreate';



const Sitebar = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => {
        let newIsOpen = !isOpen;
        setIsOpen(newIsOpen);
    }



    return ( 
        <>
        <Navbar color="faded" light expand="md">
            <NavbarBrand href="/">Welcome</NavbarBrand>
            <NavbarToggler onClick={toggle}/>
            <Collapse isOpen={isOpen} navbar>
                <Nav className="ml-auto" navbar>
                <NavItem>
                    <Button onClick={props.clickLogout}>Logout</Button>
                </NavItem>


                </Nav>
            </Collapse>
        </Navbar>



        <div className="router">
        <ul className="sidebar-list list-unstyled">
          <li><Link to="/">All Posts</Link></li>
          <li><Link to="/myposts">Functional Component</Link></li>

        </ul>


        <Switch>
          <Route exact path="/home"><Home/></Route>
          <Route exact path="/resources"><Resources /></Route>
          <Route exact path="/"><Home /></Route>
          <Route exact path="/jsxrules"><JSXRules/></Route>
          <Route exact path="/functionalcomponent"><FunctionalComponentDemo/></Route>
          <Route exact path="/state"> <State/></Route>
          <Route exact path="/effects"> <Effects/> </Route>
          <Route exact path="/propsdemo"> <PropsDemo/> </Route>

        </Switch>
        </div>


</>

);


}


export default Sitebar;
