import React, { useState } from 'react';
import { Table, Button } from 'reactstrap';

const AllPosts = (props) => {

    const URL = 'http://localhost:3001/beer/'
     const [beer, setBeer] = useState([])
    fetch(URL) .then(
        (res) => res.json()
    ).then((data) => {
        setBeer(data)
        console.log(data)
    })

const beerMapper = () => {  
return props.beer.map((beer, index) => {
return (
<div className="books">
      <div className="book" key={index}>
        <h2>{beer.name}</h2>
        <div className="details">
          <p>👨: {beer.brewery}</p>
          <p>📖: {beer.abvibu}</p>
          <p>🏘️: {beer.location}</p>
          <p>⏰: {beer.coments}</p>
        </div>
      </div>
    </div>
    )
})
} 
return (
    <> 
    <Table></Table>
    {beerMapper()}
    </>
)
}





export default AllPosts;
