import React, {useState, useEffect} from 'react'; 
import BeerCreate from './BeerCreate'   
const BeerIndex = (props) => {

    const [workouts, setWorkouts] = useState([]);

    const fetchBeer = () => {

        fetch('http://localhost:3001/beer/', {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                'Authorization': props.token
            })
        }).then(
            (res) => res.json()
        ).then((logData) => {
            setWorkouts(logData);
            console.log(logData)
        })

    }

    useEffect(() => {
        fetchBeer();
    }, [])

    return ( 
        <Container>
        <Row>
            <Col md="3">
               <BeerCreate fetchWorkouts={fetchWorkouts} token={props.token}/>
            </Col>
            <Col md="9">
               <WorkoutTable workouts={workouts} editUpdateWorkout={editUpdateWorkout} updateOn={updateOn} fetchWorkouts={fetchWorkouts} token={props.token}/>
            </Col>
            {updateActive ? <WorkoutEdit workoutToUpdate={workoutToUpdate} updateOff={updateOff} token={props.token}
            fetchWorkouts={fetchWorkouts}/> : <></>}
        </Row>
    </Container>
     );
}

export default BeerIndex;
