import React, { useState, useEffect } from 'react';  
import {
    Modal,
    ModalHeader,
    ModalBody,
    Form,
    FormGroup,
    Label,
    Input,
    Button,
  } from "reactstrap";


const BeerCreate = (props) => {
    const [name, setName] = useState('');
    const [brewery, setBrewery] = useState('');
    const [abvibu, setAbvibu] = useState('');
    const [location, setLocation] = useState();
    const [comments, setComments] = useState();



    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/beer/create', {
            method: 'POST',
            body: JSON.stringify({beer:{name: name, brewery: brewery, abvibu: abvibu, location: location, comments: comments}}),
            headers: new Headers({
                'Content-Type': 'application/json',
                'Authorization': props.token
            })
        }).then((res) => res.json())
        .then((logData) => {
            console.log(logData);
            setName('');
            setBrewery('');
            setAbvibu('');
            setLocation('');
            setComments('');
            props.fetchBeer();
        })
    }

    return (   
        <div>
    <Modal isOpen={false}>
      <ModalHeader>Post a Beer</ModalHeader>
      <ModalBody>
        <Form onSubmit={handleSubmit}>
          <FormGroup>
            <Label htmlFor="Name">Name:</Label>
            <Input
              name="name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </FormGroup>



          <Button type="submit">Post</Button>
        </Form>
      </ModalBody>
    </Modal>
  );

        </div>
      );
}

export default BeerCreate;
