import React, {useState, useEffect} from 'react'; 
import BeerCreate from './BeerCreate'   
import { Container, Row, Col } from 'reactstrap';
import APIURL from '../helpers/enviornments';
import AllPosts from './AllPosts'
import MyPosts from './MyPosts'
import { Route, Link, Switch } from 'react-router-dom'

const BeerIndex = (props) => {

    const [beer, setBeer] = useState([]);

    const fetchBeer = () => {

        fetch(`${APIURL}/beer/`, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                'Authorization': props.token
            })
        }).then(
            (res) => res.json()
        ).then((logData) => {
            setBeer(logData);
            console.log(logData)
        })

    }

    useEffect(() => {
        fetchBeer();
    }, [])

    return ( 
        <>
        <Container>
        <Row>
            <Col md="3">
            <BeerCreate fetchBeer={fetchBeer} token={props.token}/>
            </Col>
            <Col md="12">

            </Col>

        </Row>

    </Container>

    <div className="router">
<ul className="sidebar-list list-unstyled">
  <li><Link to="/">All Posts</Link></li>
  <li><Link to="/myposts">My Posts</Link></li>

</ul>


<Switch>
  <Route exact path="/allposts"><AllPosts/></Route>
  <Route exact path="/myposts"><MyPosts /></Route>


</Switch>
</div>

</>
     );
}

export default BeerIndex;
