import React, { useState, useEffect } from "react";
import {
  Modal,
  ModalHeader,
  ModalBody,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
} from "reactstrap";

const BeerEdit = (props) => {
  const beerUpdate = (event, beer) => {
    event.preventDefault();
    fetch(`http://localhost:3001/beer/update/${props.id}`, {
      method: "PUT",
      body: JSON.stringify({
        beer: {
          name: editName,
          brewery: editBrewery,
          abvibu: editAbvibu,
          location: editLocation,
          comments: editComments,
        },
      }),
      headers: new Headers({
        "Content-Type": "application/json",
        Authorization: props.token,
      }),
    }).then((res) => {
      props.fetchBeer();
      props.updateOff();
    });
  };



  useEffect(() => {
    updatingBeer();
  }, []);



  const [isOpen, setIsOpen] = useState(false);

  const showModal = () => {
    setIsOpen(true);
  };

  const hideModal = () => {
    setIsOpen(false);
  };

  return (
    <>
      <button onClick={showModal}>Edit</button>
      <Modal isOpen={isOpen}>
        <ModalHeader>Edit Post</ModalHeader>
        <ModalBody>
          <Form onSubmit={beerUpdate}>
            <FormGroup>
              <Label htmlFor="name">Name</Label>
              <Input
                 name="name"
                 value={props.editName}
                id="editName"
                onChange={(e) => setEditName(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Label htmlFor="brewery">Brewery</Label>
              <Input
                name="description"
                value={editBrewery}
                onChange={(e) => setEditBrewery(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Label htmlFor="abvibu">ABV/IBU</Label>
              <Input
                name="abvibu"
                value={editAbvibu}
                onChange={(e) => setEditAbvibu(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Label htmlFor="location">Location</Label>
              <Input
                name="location"
                value={editLocation}
                onChange={(e) => setEditLocation(e.target.value)}
              />
            </FormGroup>
            <FormGroup>
              <Label htmlFor="comments">Additional Comments</Label>
              <Input
                name="comments"
                value={editComments}
                onChange={(e) => setEditComments(e.target.value)}
              />
            </FormGroup>
            <Button
              type="submit"
              onClick={() => {
                props.editUpdateBeer(props.beer);
                props.updateOn();
              }}
              onClick={hideModal}
            >
              Post!
            </Button>
            <button onClick={hideModal}>Cancel</button>
          </Form>
        </ModalBody>
      </Modal>
    </>
  );
};

export default BeerEdit;
