import React, { useState } from "react";
import { Table, CardColumns } from "reactstrap";
import APIURL from '../helpers/enviornments';
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const MyPosts = (props) => {

  const [beer, setBeer] = useState([]);



      fetch(`${APIURL}/beer/mine`, {
          method: 'GET',
          headers: new Headers({
              'Content-Type': 'application/json',
              'Authorization': props.token
          })
      }).then(
          (res) => res.json()
      ).then((logData) => {
          setBeer(logData);
          console.log(logData)
      })


    return (
        <CardColumns>
        <Card>
          <CardImg top width="100%"  alt="Card image cap" />
          <CardBody>
            <CardTitle>{beer.name}</CardTitle>
            <CardSubtitle>{beer.brewery}</CardSubtitle>
            <CardSubtitle>{beer.abvibu}</CardSubtitle>
            <CardSubtitle>{beer.location}</CardSubtitle>
            <CardText>
              {beer.comments}
            </CardText>
            <Button>Button</Button>
          </CardBody>
        </Card>
        </CardColumns>
    )





}



export default MyPosts;
