import React, {useState, useEffect} from 'react'; 
import BeerCreate from './BeerCreate'   
import { Container, Row, Col } from 'reactstrap';
import BeerCard from './BeerCard';
import APIURL from '../helpers/enviornments';

const BeerIndex = (props) => {

    const [workouts, setWorkouts] = useState([]);

    const fetchBeer = () => {

        fetch(`${APIURL}/beer/`, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                'Authorization': props.token
            })
        }).then(
            (res) => res.json()
        ).then((logData) => {
            setWorkouts(logData);
            console.log(logData)
        })

    }

    useEffect(() => {
        fetchBeer();
    }, [])

    return ( 
        <Container>
        <Row>
            <Col md="3">
               <BeerCreate fetchBeer={fetchBeer} token={props.token}/>
            </Col>
            <Col md="9">
               <BeerCard workouts={workouts} fetchBeer={fetchBeer} token={props.token}/>
            </Col>

        </Row>
    </Container>
     );
}

export default BeerIndex;
