  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const MyPosts = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const showMine = () => {
    setIsOpen(true);
  };

  const hideMine = () => {
    setIsOpen(false);
  };

  const [beer, setBeer] = useState([]);





      return (
        <>
          <button onClick={showMine}> Show My Posts</button>
          <div className="books">
            <CardColumns>
              <Card>
                <CardImg top width="100%" alt="Card image cap" />
                <CardBody>
                  <CardTitle>{beer.name}</CardTitle>
                  <CardSubtitle>{beer.brewery}</CardSubtitle>
                  <CardSubtitle>{beer.abvibu}</CardSubtitle>
                  <CardSubtitle>{beer.location}</CardSubtitle>
                  <CardText>{beer.comments}</CardText>
                  <Button>Button</Button>
                </CardBody>
              </Card>
            </CardColumns>
          </div>
        </>
      );

  };



export default MyPosts;
