const BeerIndex = (props) => {

    const [beer, setBeer] = useState([]);

    const fetchBeer = () => {

        fetch(`${APIURL}/beer/`, {
            method: 'GET',
            headers: new Headers({
                'Content-Type': 'application/json',
                'Authorization': props.token
