import React, { useState } from "react";
import { Table, CardColumns } from "reactstrap";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const MyPosts = (props) => {


    const URL = "http://localhost:3001/beer/";
    const [beer, setBeer] = useState([]);
    fetch(URL)
      .then((res) => res.json())
      .then((data) => {
        setBeer(data);
        //console.log(data);
      });

        const beerMapper = () => {
            return props.beer.map((beer, index) => {
              return (
                <div className="books">
                    <CardColumns>
                  <Card>
                    <CardImg top width="100%"  alt="Card image cap" />
                    <CardBody>
                      <CardTitle>{beer.name}</CardTitle>
                      <CardSubtitle>{beer.brewery}</CardSubtitle>
                      <CardSubtitle>{beer.abvibu}</CardSubtitle>
                      <CardSubtitle>{beer.location}</CardSubtitle>
                      <CardText>
                        {beer.comments}
                      </CardText>
                      <Button>Button</Button>
                    </CardBody>
                  </Card>
                  </CardColumns>
                </div>
              );
            });


  };
  return (
    <>
      <Table>{beerMapper()}</Table>
    </>
  );
}



export default MyPosts;
