import React, {useState} from 'react';
import { Navbar, NavbarBrand, NavbarToggler, NavItem, Button, Nav, Collapse } from "reactstrap";
import BeerCreate from '../Beers/BeerCreate';


const Sitebar = (props) => {
    const [isOpen, setIsOpen] = useState(false);
    const toggle = () => {
        let newIsOpen = !isOpen;
        setIsOpen(newIsOpen);
    }

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);


    return ( 
        <>
        <Navbar color="faded" light expand="md">
            <NavbarBrand href="/">Welcome</NavbarBrand>
            <NavbarToggler onClick={toggle}/>
            <Collapse isOpen={isOpen} navbar>
                <Nav className="ml-auto" navbar>
                <NavItem>
                    <Button onClick={props.clickLogout}>Logout</Button>
                </NavItem>
                <NavItem>
                    <Button onClick={handleShow}>New Post</Button>
                </NavItem>
                </Nav>
            </Collapse>
        </Navbar>

<Modal isOpen={false}>
<ModalHeader>Post a Beer</ModalHeader>
<ModalBody>
  <Form onSubmit={handleSubmit}>
    <FormGroup>
      <Label htmlFor="Name">Name:</Label>
      <Input
        name="name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
    </FormGroup>



    <Button type="submit">Post</Button>
  </Form>
</ModalBody>
</Modal>


</>

);


}


export default Sitebar;
