import React,  { useState } from 'react';
import { Table, CardColumns} from "reactstrap";
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button
  } from 'reactstrap';

const MyPosts = (props) => {

    const [isOpen, setIsOpen] = useState(false);

    const showMine = () => {
      setIsOpen(true);
    };

    const hideMine = () => {
      setIsOpen(false);
    };


    fetch('http://localhost:3001/beer/mine', {
        method: 'GET',
        headers: new Headers({
            'Content-Type': 'application/json',
            'Authorization': props.token
        })
    }).then(
        (res) => res.json()
    ).then((logData) => {
        setMine(logData);
       // console.log(logData)
    })

    const [mine, setMine] = useState([]);
const myMapper = ( ) => {
    return props.mine.map((mine, index) => {
    return ( 
        <>
        <button onClick={showMine}> Show My Posts</button>
        <div className="books">
        <CardColumns>
      <Card>
        <CardImg top width="100%"  alt="Card image cap" />
        <CardBody>
          <CardTitle>{mine.name}</CardTitle>
          <CardSubtitle>{mine.brewery}</CardSubtitle>
          <CardSubtitle>{mine.abvibu}</CardSubtitle>
          <CardSubtitle>{mine.location}</CardSubtitle>
          <CardText>
            {mine.comments}
          </CardText>
          <Button>Button</Button>
        </CardBody>
      </Card>
      </CardColumns>
    </div>
    </>
        );
    })
};

return (
    <>
      <Table>{myMapper()}</Table>
    </>
  );

}



export default MyPosts;
