import React, { useState } from "react";
import { Table, CardColumns } from "reactstrap";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const MyPosts = (props) => {
  const [isOpen, setIsOpen] = useState(false);

  const showMine = () => {
    setIsOpen(true);
  };

  const hideMine = () => {
    setIsOpen(false);
  };

  fetch("http://localhost:3001/beer/mine", {
    method: "GET",
    headers: new Headers({
      "Content-Type": "application/json",
      Authorization: props.token,
    }),
  })
    .then((res) => res.json())
    .then((logData) => {
      setBeer(logData);
      // console.log(logData)
    });
  const [beer, setBeer] = useState([]);

  const myMapper = () => {

    return props.beer.map((mine, index) => {

      return (
        <>
          <button onClick={showMine}> Show My Posts</button>
          <div className="books">
            <CardColumns>
              <Card>
                <CardImg top width="100%" alt="Card image cap" />
                <CardBody>
                  <CardTitle>{beer.name}</CardTitle>
                  <CardSubtitle>{beer.brewery}</CardSubtitle>
                  <CardSubtitle>{beer.abvibu}</CardSubtitle>
                  <CardSubtitle>{beer.location}</CardSubtitle>
                  <CardText>{beer.comments}</CardText>
                  <Button>Button</Button>
                </CardBody>
              </Card>
            </CardColumns>
          </div>
        </>
      );
    });
  };

  return (
    <>
      <Table>{myMapper()}</Table>
    </>
  );
};

export default MyPosts;
