import React, { useState } from "react";
import { Table, CardColumns } from "reactstrap";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button,
} from "reactstrap";

const MyPosts = (props) => {
    const [beer, setBeer] = useState([]);

    fetch('http://localhost:3001/beer/mine/', {
        method: 'GET',
        headers: new Headers({
            'Content-Type': 'application/json',
            'Authorization': props.token
        })
    }).then(
        (res) => res.json()
    ).then((logData) => {
        setBeer(logData);
        console.log(logData)
    })

        const beerMapper = () => {
            return props.beer.map((beer, index) => {
              return (
                <div className="books">
                    <CardColumns>
                  <Card>
                    <CardImg top width="100%"  alt="Card image cap" />
                    <CardBody>
                      <CardTitle>{beer.name}</CardTitle>
                      <CardSubtitle>{beer.brewery}</CardSubtitle>
                      <CardSubtitle>{beer.abvibu}</CardSubtitle>
                      <CardSubtitle>{beer.location}</CardSubtitle>
                      <CardText>
                        {beer.comments}
                      </CardText>
                      <Button>Button</Button>
                    </CardBody>
                  </Card>
                  </CardColumns>
                </div>
              );
            });


  };
  return (
    <>
      <Table>{beerMapper()}</Table>
    </>
  );
}



export default MyPosts;
