  const [results, setResults] = useState([]);

  useEffect(() => {
      handleClick();
  },[]);



  function displayCards(){
      return(
          results.map((result, index) => <BeerCard key={index} 
          character = {result}/>)
      )
  }
  return ( 
      <div>
      {/* <button onClick={handleClick}> Click Here</button> */}
      <CardColumns>
      {displayCards()}
