    const [name, setName] = useState('');
    const [brewery, setBrewery] = useState('');
    const [abvibu, setAbvibu] = useState('');
    const [location, setLocation] = useState();
    const [comments, setComments] = useState();

    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/beer/create', {
            method: 'POST',
            body: JSON.stringify({beer:{name: name, brewery: brewery, abvibu: abvibu, location: location, comments: comments}}),
            headers: new Headers({
                'Content-Type': 'application/json',
                'Authorization': props.token
            })
        }).then((res) => res.json())
        .then((logData) => {
            console.log(logData);
            setName('');
            setBrewery('');
            setAbvibu('');
            setLocation('');
            setComments('');
            props.fetchBeer();
        })
    }

